	text-align: center;
	margin-top: 60px;
	margin-bottom: 40px;
	background-color: orange;
}

h3 {
	text-align: center;
	margin-top: 30px;
	margin-bottom: 10px;
	color: #ff4f12;
}



/* Menú de navegación */

.contHeader {
	margin: auto;
	text-align: center;
	background: linear-gradient(#d0ece7 5%, white 85%);
	position: fixed;
	width: 100%;
	z-index: 99;
}

nav ul {
	list-style: none;
	padding: 0;
}

nav ul li {
	display: inline-block;
	vertical-align: middle;
	padding: 8px 20px;
	margin: 10px 12px;
}

nav ul li a {
	text-decoration: none;
	font-family: 'Montserrat Alternates', sans-serif;
	color:  #424949;
	transition: 0.6s;
}

.activo a{
	color: #138d75;
	border-bottom: 2px solid #138d75;
}

