	width: 30%;
	display: block;
	margin: auto;
	padding-top: 30px;
	padding-bottom: 30px;
}

p {
	text-indent: 25px;
	line-height: 30px;
	margin-top: 14px;
}

.iconosCentro {
	display: flex;
}

.icono {
	text-align: center;
	font-size: 30px;
	color: #ff4f12;
	margin-top: 18px;
	margin-bottom: 18px;
	width: 90%;
}
.iconoFlecha {
	text-align: right;
	font-size: 30px;
	color: #ff4f12;
	margin-top: 18px;
	margin-bottom: 18px;
	width: 10%;
}

.imagen img {
	width: 70%;
}

code {
	font-size: 15px;
	line-height: 18px;
}
cite {
	font-size: 0.9em;
}

.principal iframe {
	display: block;
	margin: auto;
	margin-top: 45px;
	margin-bottom: 45px;
}

/* ------  FOOTER  ------*/
footer {
	/*max-width: 1200px;*/
	/*margin: auto;*/
	text-align: center;
	margin-top: 25px;
	padding-top: 8px;
	padding-bottom: 15px;
	border-top: 1px solid grey;
	font-size: 14px;
}

#text{
	font-size: 20px;
	color: #ff6800;
	text-align: center;
	padding-top: 35px;
	padding-bottom: 35px;
}

/* ----- @media para adaptación a tablet y móvil ---- */
/* ---- MEDIA-QUERY adaptación a 820px ---- */

@media (max-width: 880px) {
	.principal {
		max-width: 90%;
	}
}

@media (max-width: 780px){
	h1 {
		font-size: 32px;
	}

	nav ul li a{
		font-size: 18px;
	}

	h1{
		margin-top: 20px;
		margin-bottom: 10px;
	}
}

@media (max-width: 680px) {
	.contHeader{
		position: relative;
	}

	main{
		padding-top: 26px;
	}

	h1{
		margin-top: 0px;
		padding-top: 20px;
	}

	p {
		font-size: 16px;
	}

	.principal img {
		width: 85%;
	}

	iframe {
		width: 90%;
	}

	footer p{
		font-size: 12px;
	}
}

@media (max-width: 560px) {
	p {
		font-size: 12px;
	}

	h1 {
		font-size: 28px;
	}
	h2 {
		font-size: 24px;
	}
	h3 {
		font-size: 20px;
	}
	.principal {
		width: 95%;
	}
	.principal img {
		width: 95%;
	}
	footer p {
		font-size: 10px;
		line-height: 20px;
	}

	code{
		font-size: 12px;
	}
	iframe{
		width: 95%;
	}

	#text{
		font-size: 16px;
		padding-bottom: 20px;
	}
}
@media (max-width: 480px){
	h1{
		font-size: 22px;
	}
	h2{
		font-size: 20px;	
	}
	h3{
		font-size: 17px;
	}

	nav ul li{
		margin-top: 0px;
		padding-top: 5px;
		margin-bottom: 0px;
		padding-bottom: 5px;
	}
	nav ul li a{
		font-size: 15px;
	}
}

